import React from "react";

const Home = () => {
  return (
    <div className="relative h-screen overflow-hidden">
      {/* Background Video */}
      <video
        className="absolute top-0 left-0 w-full h-full object-cover"
        src="/background.mp4"
        autoPlay
        loop
        muted
      />

      {/* Overlay for content */}
      <div className="relative z-10 flex flex-col items-center justify-center h-full bg-black bg-opacity-50 text-white">
        {/* Title */}
        <h1 className="text-6xl md:text-8xl font-bold mb-4">CHAMPION SPHERE</h1>

        {/* Description Box */}
        <div className="bg-white text-black p-4 rounded-lg shadow-lg">
          <p className="text-lg">Your description goes here</p>
        </div>

        {/* Animated Logo */}
        <img
          className="w-32 h-32 mt-8 animate-bounce"
          src="path/to/logo.png"
          alt="Logo"
        />
      </div>
    </div>
  );
};

export default Home;
